function getWebLink(pageUrl, which = "email") {
  if (which === "pages") {
    return `${window.location.protocol}//${window.location.host}/${pageUrl}`;
  } else if (which === "whatsapp") {
    return `https://wa.me/${pageUrl}`;
  } else if (which === "call") {
    return `tel:${pageUrl}`;
  } else {
    return `mailto:${pageUrl}`;
  }
}

